// Animation Functions
function pageTransition() {
    var tl = gsap.timeline()
    tl.to('ul.transition li', { duration: .2, scaleY: 1, transformOrigin: "bottom", stagger: .1 })
    tl.to('ul.transition li', { duration: .4, scaleY: 0, transformOrigin: "bottom", stagger: .1, delay: 0 })
}

function contentAnimation() {
    console.log("test")
    var tl = gsap.timeline()
}



// Dom selectors
const btnHamburger = document.querySelector('#btnHamburger')
const header = document.querySelector('.header')
const body = document.querySelector('body')
const overlay = document.querySelector('.overlay')
const fadeElems = document.querySelectorAll('.has-fade')
const arrow = $('#arrow')
const target = document.querySelector('#js-target');
const leesMeer = $('#lees__meer')

// Splide Init
const config = {
    type: "loop"
}

var elms = document.getElementsByClassName('splide');
for (var i = 0, len = elms.length; i < len; i++) {
    new Splide(elms[i], config).mount();
}

// Locomotive Init
import LocomotiveScroll from 'locomotive-scroll';

const scroll = new LocomotiveScroll({
    el: document.querySelector('[data-scroll-container]'),
    smooth: true
});

arrow.click(function() {
    scroll.scrollTo(target);
})

leesMeer.click(function(e) {
    e.preventDefault();
    scroll.scrollTo(target);
})



// Mobile hamburger animations
btnHamburger.addEventListener('click', () => {

    if (header.classList.contains('open')) { // Close
        body.classList.remove('noscroll')
        header.classList.remove('open')
        fadeElems.forEach(function(elem) {
            elem.classList.remove('fade-in')
            elem.classList.add('fade-out')
        })


    } else { // Open
        body.classList.add('noscroll')
        header.classList.add('open')
        fadeElems.forEach(function(elem) {
            elem.classList.remove('fade-out')
            elem.classList.add('fade-in')
        })
    }
})

//Barba
// import barba from '@barba/core';

function delay(n) {
    n = n || 2000
    return new Promise(done => {
        setTimeout(() => {
            done()
        }, n)
    })
}

barba.init({
    transitions: [{
        sync: true,
        async leave(data) {
            console.log("hello");
            const done = this.async()
            pageTransition()
            await delay(1500)
            done()
        }
    }]
})
